import React, { useState } from "react";
import { Search, CheckCircle, Clock, AlertCircle, MapPin, Building, FileText, User, Calendar, Hash } from "lucide-react";

export default function TrackStatus() {
  const [complaintId, setComplaintId] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleTrack = async () => {
    if (!complaintId.trim()) return;
    
    setIsLoading(true);
    try {
      // Replace axios with fetch for demo purposes
      const res = await fetch(`http://localhost:5000/api/complaint/${complaintId.trim()}`);
      const data = await res.json();

      if (data.success) {
        setResult(data);
        setError("");
      } else {
        setResult(null);
        setError("शिकायत नहीं मिली। कृपया सही Complaint ID दर्ज करें।");
      }
    } catch (err) {
      setResult(null);
      setError("सर्वर से कनेक्ट नहीं हो सका या शिकायत नहीं मिली।");
    } finally {
      setIsLoading(false);
    }
  };

  const getStatusConfig = (status) => {
    const statusLower = status?.toLowerCase() || '';
    if (statusLower.includes('पूर्ण') || statusLower.includes('complete') || statusLower.includes('resolved')) {
      return {
        color: 'from-green-500 to-emerald-600',
        bgColor: 'bg-green-500',
        textColor: 'text-green-700',
        icon: CheckCircle,
        progress: 100
      };
    } else if (statusLower.includes('प्रगति') || statusLower.includes('progress') || statusLower.includes('pending')) {
      return {
        color: 'from-yellow-500 to-orange-500',
        bgColor: 'bg-yellow-500',
        textColor: 'text-yellow-700',
        icon: Clock,
        progress: 60
      };
    }
    return {
      color: 'from-blue-500 to-indigo-600',
      bgColor: 'bg-blue-500',
      textColor: 'text-blue-700',
      icon: AlertCircle,
      progress: 30
    };
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-800">
      {/* Header Section */}
      <div className="bg-white/10 backdrop-blur-md border-b border-white/20">
        <div className="max-w-4xl mx-auto px-6 py-8">
          <div className="text-center">
            <div className="inline-flex items-center justify-center w-20 h-20 bg-white/20 rounded-full mb-4 backdrop-blur-sm">
              <Search className="w-10 h-10 text-white" />
            </div>
            <h1 className="text-4xl font-bold text-white mb-2">शिकायत ट्रैकर</h1>
            <p className="text-white/80 text-lg">अपनी शिकायत की वर्तमान स्थिति का पता लगाएं</p>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-6 py-12">
        {/* Search Section */}
        <div className="bg-white/15 backdrop-blur-md rounded-3xl p-8 mb-8 border border-white/20">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1 relative">
              <Hash className="absolute left-4 top-1/2 transform -translate-y-1/2 text-white/60 w-5 h-5" />
              <input
                type="text"
                placeholder="अपनी Complaint ID यहाँ दर्ज करें"
                className="w-full pl-12 pr-4 py-4 bg-white/20 backdrop-blur-sm border border-white/30 rounded-2xl text-white placeholder-white/60 focus:outline-none focus:border-white/60 focus:bg-white/25 transition-all duration-300 text-lg"
                value={complaintId}
                onChange={(e) => setComplaintId(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleTrack()}
              />
            </div>
            <button
              onClick={handleTrack}
              disabled={isLoading || !complaintId.trim()}
              className="px-8 py-4 bg-gradient-to-r from-orange-500 to-pink-600 hover:from-orange-600 hover:to-pink-700 disabled:from-gray-500 disabled:to-gray-600 text-white font-semibold rounded-2xl shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300 disabled:transform-none flex items-center justify-center gap-3 min-w-[160px]"
            >
              {isLoading ? (
                <>
                  <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white"></div>
                  खोज रहे हैं
                </>
              ) : (
                <>
                  <Search className="w-5 h-5" />
                  ट्रैक करें
                </>
              )}
            </button>
          </div>

          {error && (
            <div className="mt-6 p-4 bg-red-500/20 backdrop-blur-sm border border-red-400/30 rounded-xl">
              <div className="flex items-center gap-3">
                <AlertCircle className="w-6 h-6 text-red-300 flex-shrink-0" />
                <p className="text-red-100 font-medium">{error}</p>
              </div>
            </div>
          )}
        </div>

        {/* Result Section */}
        {result && (
          <div className="space-y-6 animate-slideUp">
            {/* Status Card */}
            <div className="bg-white rounded-3xl shadow-2xl overflow-hidden">
              <div className={`bg-gradient-to-r ${getStatusConfig(result.status).color} p-6`}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-4">
                    <div className="bg-white/20 p-3 rounded-2xl">
                      {React.createElement(getStatusConfig(result.status).icon, {
                        className: "w-8 h-8 text-white"
                      })}
                    </div>
                    <div>
                      <h2 className="text-2xl font-bold text-white mb-1">शिकायत की स्थिति</h2>
                      <p className="text-white/90 text-lg font-semibold">{result.status}</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="bg-white/20 rounded-xl px-4 py-2">
                      <p className="text-white/80 text-sm">ID: {complaintId}</p>
                    </div>
                  </div>
                </div>
                
                {/* Progress Bar */}
                <div className="mt-6">
                  <div className="bg-white/20 rounded-full h-3 overflow-hidden">
                    <div 
                      className="bg-white/80 h-full rounded-full transition-all duration-1000 ease-out"
                      style={{ width: `${getStatusConfig(result.status).progress}%` }}
                    ></div>
                  </div>
                  <p className="text-white/80 text-sm mt-2">{getStatusConfig(result.status).progress}% पूर्ण</p>
                </div>
              </div>
            </div>

            {/* Details Grid */}
            <div className="grid md:grid-cols-2 gap-6">
              <div className="bg-white rounded-2xl p-6 shadow-xl hover:shadow-2xl transition-all duration-300 hover:-translate-y-1">
                <div className="flex items-start gap-4">
                  <div className="bg-blue-100 p-3 rounded-xl">
                    <User className="w-6 h-6 text-blue-600" />
                  </div>
                  <div className="flex-1">
                    <h3 className="text-gray-600 text-sm font-medium mb-1">शिकायतकर्ता</h3>
                    <p className="text-xl font-bold text-gray-900">{result.name}</p>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-2xl p-6 shadow-xl hover:shadow-2xl transition-all duration-300 hover:-translate-y-1">
                <div className="flex items-start gap-4">
                  <div className="bg-green-100 p-3 rounded-xl">
                    <MapPin className="w-6 h-6 text-green-600" />
                  </div>
                  <div className="flex-1">
                    <h3 className="text-gray-600 text-sm font-medium mb-1">स्थान</h3>
                    <p className="text-xl font-bold text-gray-900">{result.location}</p>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-2xl p-6 shadow-xl hover:shadow-2xl transition-all duration-300 hover:-translate-y-1">
                <div className="flex items-start gap-4">
                  <div className="bg-purple-100 p-3 rounded-xl">
                    <Building className="w-6 h-6 text-purple-600" />
                  </div>
                  <div className="flex-1">
                    <h3 className="text-gray-600 text-sm font-medium mb-1">विभाग</h3>
                    <p className="text-xl font-bold text-gray-900">{result.department}</p>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-2xl p-6 shadow-xl hover:shadow-2xl transition-all duration-300 hover:-translate-y-1">
                <div className="flex items-start gap-4">
                  <div className="bg-orange-100 p-3 rounded-xl">
                    <Calendar className="w-6 h-6 text-orange-600" />
                  </div>
                  <div className="flex-1">
                    <h3 className="text-gray-600 text-sm font-medium mb-1">दिनांक</h3>
                    <p className="text-xl font-bold text-gray-900">
                      {new Date().toLocaleDateString('hi-IN')}
                    </p>
                  </div>
                </div>
              </div>
            </div>

            {/* Description Card */}
            <div className="bg-white rounded-2xl p-8 shadow-xl">
              <div className="flex items-start gap-4 mb-4">
                <div className="bg-indigo-100 p-3 rounded-xl">
                  <FileText className="w-6 h-6 text-indigo-600" />
                </div>
                <div>
                  <h3 className="text-xl font-bold text-gray-900 mb-2">शिकायत का विवरण</h3>
                  <p className="text-gray-700 leading-relaxed text-lg">{result.description}</p>
                </div>
              </div>
            </div>

            {/* Timeline Preview */}
            <div className="bg-white rounded-2xl p-8 shadow-xl">
              <h3 className="text-xl font-bold text-gray-900 mb-6">प्रक्रिया टाइमलाइन</h3>
              <div className="space-y-4">
                <div className="flex items-center gap-4">
                  <div className="bg-green-500 w-4 h-4 rounded-full"></div>
                  <div>
                    <p className="font-semibold text-gray-900">शिकायत दर्ज की गई</p>
                    <p className="text-gray-600 text-sm">आपकी शिकायत सफलतापूर्वक दर्ज हो गई है</p>
                  </div>
                </div>
                <div className="flex items-center gap-4">
                  <div className={`w-4 h-4 rounded-full ${getStatusConfig(result.status).progress >= 60 ? 'bg-green-500' : 'bg-gray-300'}`}></div>
                  <div>
                    <p className="font-semibold text-gray-900">समीक्षा के अधीन</p>
                    <p className="text-gray-600 text-sm">संबंधित विभाग द्वारा समीक्षा की जा रही है</p>
                  </div>
                </div>
                <div className="flex items-center gap-4">
                  <div className={`w-4 h-4 rounded-full ${getStatusConfig(result.status).progress === 100 ? 'bg-green-500' : 'bg-gray-300'}`}></div>
                  <div>
                    <p className="font-semibold text-gray-900">समाधान</p>
                    <p className="text-gray-600 text-sm">शिकायत का समाधान हो गया है</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateY(40px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .animate-slideUp {
          animation: slideUp 0.6s ease-out;
        }
      `}</style>
    </div>
  );
}
