import React, { useState } from "react";
import { Search, CheckCircle, Clock, AlertCircle, MapPin, Building, User, FileText, Loader } from "lucide-react";

export default function TrackComplaint() {
  const [complaintId, setComplaintId] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleTrack = async () => {
    if (!complaintId.trim()) return;

    setLoading(true);
    setError("");
    setResult(null);

    try {
      // Replace with your actual axios import and API call
      // const res = await axios.get(`http://localhost:5000/api/complaint/${complaintId.trim()}`);
      
      // Temporary placeholder - remove this line and uncomment above
      throw new Error("Please add your axios import and API call");
      
      /*
      if (res.data.success) {
        setResult(res.data);
        setError("");
      } else {
        setResult(null);
        setError("शिकायत नहीं मिली। कृपया सही Complaint ID दर्ज करें।");
      }
      */
    } catch (err) {
      setResult(null);
      setError("सर्वर से कनेक्ट नहीं हो सका या शिकायत नहीं मिली।");
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    const lowerStatus = status?.toLowerCase() || "";
    if (lowerStatus.includes("completed") || lowerStatus.includes("resolved") || lowerStatus.includes("पूर्ण")) {
      return <CheckCircle className="w-5 h-5 text-green-500" />;
    } else if (lowerStatus.includes("progress") || lowerStatus.includes("प्रगति")) {
      return <Clock className="w-5 h-5 text-yellow-500" />;
    } else {
      return <AlertCircle className="w-5 h-5 text-orange-500" />;
    }
  };

  const getStatusColor = (status) => {
    const lowerStatus = status?.toLowerCase() || "";
    if (lowerStatus.includes("completed") || lowerStatus.includes("resolved") || lowerStatus.includes("पूर्ण")) {
      return "bg-green-50 border-green-200 text-green-800";
    } else if (lowerStatus.includes("progress") || lowerStatus.includes("प्रगति")) {
      return "bg-yellow-50 border-yellow-200 text-yellow-800";
    } else {
      return "bg-orange-50 border-orange-200 text-orange-800";
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-indigo-50 py-12 px-4">
      <div className="max-w-lg mx-auto">
        
        {/* Header Section */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-2xl mb-4 shadow-lg">
            <Search className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            शिकायत ट्रैकिंग
          </h1>
          <p className="text-gray-600">
            अपनी शिकायत की वर्तमान स्थिति जानें
          </p>
        </div>

        {/* Main Card */}
        <div className="bg-white rounded-3xl shadow-xl border border-gray-100 overflow-hidden">
          
          {/* Search Section */}
          <div className="p-8 bg-gradient-to-r from-blue-600 to-indigo-600">
            <div className="relative">
              <input
                type="text"
                placeholder="यहाँ Complaint ID दर्ज करें"
                className="w-full pl-12 pr-4 py-4 rounded-2xl border-0 focus:outline-none focus:ring-4 focus:ring-blue-200 text-gray-900 placeholder-gray-500 shadow-inner text-lg"
                value={complaintId}
                onChange={(e) => setComplaintId(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleTrack()}
              />
              <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            </div>

            <button
              onClick={handleTrack}
              disabled={loading || !complaintId.trim()}
              className="w-full mt-4 bg-white text-blue-600 py-4 rounded-2xl font-semibold shadow-lg hover:shadow-xl transform hover:scale-[1.02] transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none flex items-center justify-center space-x-2"
            >
              {loading ? (
                <>
                  <Loader className="w-5 h-5 animate-spin" />
                  <span>खोजा जा रहा है...</span>
                </>
              ) : (
                <>
                  <Search className="w-5 h-5" />
                  <span>स्थिति जांचें</span>
                </>
              )}
            </button>
          </div>

          {/* Results Section */}
          <div className="p-8">
            {/* Error State */}
            {error && (
              <div className="bg-red-50 border-l-4 border-red-400 p-6 rounded-lg">
                <div className="flex items-center">
                  <AlertCircle className="w-6 h-6 text-red-400 mr-3" />
                  <p className="text-red-800 font-medium">{error}</p>
                </div>
              </div>
            )}

            {/* Success State */}
            {result && (
              <div className="space-y-6">
                {/* Status Badge */}
                <div className={`inline-flex items-center space-x-2 px-4 py-2 rounded-full border ${getStatusColor(result.status)}`}>
                  {getStatusIcon(result.status)}
                  <span className="font-semibold">स्थिति: {result.status}</span>
                </div>

                {/* Details Grid */}
                <div className="grid gap-4">
                  <div className="flex items-start space-x-3 p-4 bg-gray-50 rounded-xl">
                    <User className="w-5 h-5 text-gray-500 mt-0.5 flex-shrink-0" />
                    <div>
                      <p className="text-sm text-gray-500 mb-1">शिकायतकर्ता</p>
                      <p className="font-semibold text-gray-900">{result.name}</p>
                    </div>
                  </div>

                  <div className="flex items-start space-x-3 p-4 bg-gray-50 rounded-xl">
                    <MapPin className="w-5 h-5 text-gray-500 mt-0.5 flex-shrink-0" />
                    <div>
                      <p className="text-sm text-gray-500 mb-1">स्थान</p>
                      <p className="font-semibold text-gray-900">{result.location}</p>
                    </div>
                  </div>

                  <div className="flex items-start space-x-3 p-4 bg-gray-50 rounded-xl">
                    <Building className="w-5 h-5 text-gray-500 mt-0.5 flex-shrink-0" />
                    <div>
                      <p className="text-sm text-gray-500 mb-1">विभाग</p>
                      <p className="font-semibold text-gray-900">{result.department}</p>
                    </div>
                  </div>

                  <div className="flex items-start space-x-3 p-4 bg-gray-50 rounded-xl">
                    <FileText className="w-5 h-5 text-gray-500 mt-0.5 flex-shrink-0" />
                    <div>
                      <p className="text-sm text-gray-500 mb-1">विवरण</p>
                      <p className="font-medium text-gray-900 leading-relaxed">{result.description}</p>
                    </div>
                  </div>
                </div>

                {/* Progress Indicator */}
                <div className="mt-8 p-6 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl border border-blue-100">
                  <h3 className="font-semibold text-gray-900 mb-3 flex items-center">
                    <Clock className="w-5 h-5 mr-2 text-blue-600" />
                    प्रगति अपडेट
                  </h3>
                  <div className="flex items-center space-x-4">
                    <div className="flex-1 bg-gray-200 rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full transition-all duration-1000 ${
                          result.status?.toLowerCase().includes('completed') || result.status?.toLowerCase().includes('पूर्ण') 
                            ? 'bg-green-500 w-full' 
                            : result.status?.toLowerCase().includes('progress') || result.status?.toLowerCase().includes('प्रगति')
                            ? 'bg-yellow-500 w-2/3'
                            : 'bg-orange-500 w-1/3'
                        }`}
                      ></div>
                    </div>
                    <span className="text-sm font-medium text-gray-600">
                      {result.status?.toLowerCase().includes('completed') || result.status?.toLowerCase().includes('पूर्ण') 
                        ? '100%' 
                        : result.status?.toLowerCase().includes('progress') || result.status?.toLowerCase().includes('प्रगति')
                        ? '66%'
                        : '33%'
                      }
                    </span>
                  </div>
                </div>
              </div>
            )}

            {/* Empty State */}
            {!result && !error && !loading && (
              <div className="text-center py-12">
                <div className="w-16 h-16 mx-auto mb-4 bg-gray-100 rounded-full flex items-center justify-center">
                  <Search className="w-8 h-8 text-gray-400" />
                </div>
                <p className="text-gray-500">
                  शिकायत की स्थिति देखने के लिए Complaint ID दर्ज करें
                </p>
              </div>
            )}
          </div>
        </div>

        {/* Footer Info */}
        <div className="mt-8 text-center">
          <p className="text-sm text-gray-500">
            सहायता के लिए संपर्क करें: <span className="font-semibold">1800-XXX-XXXX</span>
          </p>
        </div>
      </div>
    </div>
  );
}
