import React, { useState } from "react";
import axios from "axios";

export default function LoginForm({ onLogin }) {
  const [form, setForm] = useState({ identifier: "", password: "" });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // send identifier as email/phone
      const payload = form.identifier.includes("@")
        ? { email: form.identifier, password: form.password }
        : { phone: form.identifier, password: form.password };

      const res = await axios.post("http://localhost:5000/api/auth/login", payload);
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("userId", res.data.userId);
      localStorage.setItem("name", res.data.name);
      localStorage.setItem("email", res.data.email);
      localStorage.setItem("phone", res.data.phone);
      localStorage.setItem("address", res.data.address);
      if (res.data.photo) localStorage.setItem("photo", res.data.photo);

      onLogin && onLogin();
      alert("Login successful!");
    } catch (err) {
      alert(err.response?.data?.error || "Login failed");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-semibold mb-4">Login</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="identifier"
          value={form.identifier}
          onChange={handleChange}
          placeholder="Email or Phone"
          required
          className="w-full border px-3 py-2 rounded"
        />
        <input
          type="password"
          name="password"
          value={form.password}
          onChange={handleChange}
          placeholder="Password"
          required
          className="w-full border px-3 py-2 rounded"
        />
        <button type="submit" className="w-full bg-blue-600 text-white px-4 py-2 rounded">
          Login
        </button>
      </form>
    </div>
  );
}
