import React, { useState } from "react";
import axios from "axios";

export default function LoginForm() {
  const [form, setForm] = useState({
    identifier: "", // email or phone
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        email: form.identifier, // backend accepts email OR phone
        password: form.password,
      });

      alert(`Welcome, ${res.data.name}`);
      // Save userId in localStorage for session if needed
      localStorage.setItem("userId", res.data.userId);
    } catch (err) {
      alert(err.response?.data?.message || "Login failed");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-semibold mb-4">Login</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input name="identifier" value={form.identifier} onChange={handleChange} placeholder="Email or Phone" required className="w-full border px-3 py-2 rounded" />
        <input type="password" name="password" value={form.password} onChange={handleChange} placeholder="Password" required className="w-full border px-3 py-2 rounded" />
        <button type="submit" className="w-full bg-green-600 text-white px-4 py-2 rounded">Login</button>
      </form>
    </div>
  );
}
