import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { LineChart, Line, BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

export default function UserDashboard() {
  const navigate = useNavigate();

  const [user, setUser] = useState(null);
  const [userStats, setUserStats] = useState({
    totalComplaints: 0,
    pending: 0,
    resolved: 0,
    inProgress: 0
  });
  const [recentComplaints, setRecentComplaints] = useState([]);
  const [activeSection, setActiveSection] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [userPoints, setUserPoints] = useState(0);
  const [userBadges, setUserBadges] = useState([]);

  // Sample data for charts
  const [monthlyData, setMonthlyData] = useState([
    { month: 'Jan', filed: 2, resolved: 1 },
    { month: 'Feb', filed: 3, resolved: 2 },
    { month: 'Mar', filed: 1, resolved: 3 },
    { month: 'Apr', filed: 4, resolved: 2 },
    { month: 'May', filed: 2, resolved: 4 },
    { month: 'Jun', filed: 3, resolved: 1 }
  ]);

  const pieData = [
    { name: 'Resolved', value: userStats.resolved, color: '#10B981' },
    { name: 'In Progress', value: userStats.inProgress, color: '#8B5CF6' },
    { name: 'Pending', value: userStats.pending, color: '#F59E0B' }
  ];

  // Fetch user data and complaints on component mount
  useEffect(() => {
    const checkAuthentication = () => {
      const token = localStorage.getItem('token') || localStorage.getItem('authToken');
      const isUser = localStorage.getItem('isUser');
      
      if (!token || !isUser) {
        navigate('/login');
        return false;
      }
      return true;
    };

    if (checkAuthentication()) {
      fetchUserData();
      fetchUserComplaints();
      calculateUserPoints();
    }
  }, [navigate]);

  const fetchUserData = async () => {
    try {
      const storedUserData = localStorage.getItem('userData');
      if (storedUserData) {
        const userData = JSON.parse(storedUserData);
        setUser(userData);
        setLoading(false);
      }

      const token = localStorage.getItem('token') || localStorage.getItem('authToken');
      const userId = JSON.parse(storedUserData || '{}').userId;

      if (userId && token) {
        const response = await axios.get(`http://localhost:5000/api/users/${userId}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (response.data.success) {
          const freshUserData = response.data.user;
          setUser(freshUserData);
          localStorage.setItem('userData', JSON.stringify(freshUserData));
        }
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
      const storedUserData = localStorage.getItem('userData');
      if (storedUserData) {
        setUser(JSON.parse(storedUserData));
      } else {
        handleLogout();
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchUserComplaints = async () => {
    try {
      const storedUserData = localStorage.getItem('userData');
      const userId = JSON.parse(storedUserData || '{}').userId;
      const token = localStorage.getItem('token') || localStorage.getItem('authToken');

      if (!userId || !token) return;

      const response = await axios.get(`http://localhost:5000/api/complaints/user/${userId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        const complaints = response.data.complaints;
        
        const stats = {
          totalComplaints: complaints.length,
          pending: complaints.filter(c => c.status === 'Pending').length,
          resolved: complaints.filter(c => c.status === 'Resolved').length,
          inProgress: complaints.filter(c => c.status === 'In Progress').length
        };
        
        setUserStats(stats);
        
        const recent = complaints
          .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
          .slice(0, 5);
        setRecentComplaints(recent);
      }
    } catch (error) {
      console.error('Error fetching complaints:', error);
      setError('Failed to load complaint data');
    }
  };

  const calculateUserPoints = () => {
    const points = (userStats.resolved * 10) + (userStats.totalComplaints * 2);
    setUserPoints(points);

    const badges = [];
    if (userStats.resolved >= 5) badges.push({ name: 'Problem Solver', icon: 'ðŸ†', color: 'gold' });
    if (userStats.totalComplaints >= 3) badges.push({ name: 'Active Citizen', icon: 'ðŸŒŸ', color: 'blue' });
    if (userStats.resolved >= 10) badges.push({ name: 'Champion', icon: 'ðŸ‘‘', color: 'purple' });
    if (userStats.totalComplaints >= 10) badges.push({ name: 'Community Helper', icon: 'ðŸ¤', color: 'green' });
    
    setUserBadges(badges);
  };

  useEffect(() => {
    calculateUserPoints();
  }, [userStats]);

  const handleLogout = () => {
    localStorage.removeItem("isUser");
    localStorage.removeItem("userData");
    localStorage.removeItem("authToken");
    localStorage.removeItem("token");
    navigate("/login");
  };

  const menuItems = [
    {
      id: 'dashboard',
      label: 'Dashboard',
      hindiLabel: 'à¤¡à¥ˆà¤¶à¤¬à¥‹à¤°à¥à¤¡',
      icon: 'ðŸ ',
      path: '/dashboard'
    },
    {
      id: 'voice-complaint',
      label: 'Voice Complaint',
      hindiLabel: 'à¤†à¤µà¤¾à¤œà¤¼ à¤¶à¤¿à¤•à¤¾à¤¯à¤¤',
      icon: 'ðŸŽ¤',
      path: '/voice'
    },
    {
      id: 'written-complaint',
      label: 'Written Complaint',
      hindiLabel: 'à¤²à¤¿à¤–à¤¿à¤¤ à¤¶à¤¿à¤•à¤¾à¤¯à¤¤',
      icon: 'ðŸ“',
      path: '/complaint'
    },
    {
      id: 'track-complaint',
      label: 'Track Complaint',
      hindiLabel: 'à¤¶à¤¿à¤•à¤¾à¤¯à¤¤ à¤Ÿà¥à¤°à¥ˆà¤• à¤•à¤°à¥‡à¤‚',
      icon: 'ðŸ”',
      path: '/trackstatus'
    },
    {
      id: 'emergency',
      label: 'Emergency Support',
      hindiLabel: 'à¤†à¤ªà¤¾à¤¤à¤•à¤¾à¤²à¥€à¤¨ à¤¸à¤¹à¤¾à¤¯à¤¤à¤¾',
      icon: 'ðŸš¨',
      path: '/women-child-complaint'
    },
    {
      id: 'profile',
      label: 'Profile',
      hindiLabel: 'à¤ªà¥à¤°à¥‹à¤«à¤¾à¤‡à¤²',
      icon: 'ðŸ‘¤',
      path: '/profile'
    },
    {
      id: 'help',
      label: 'Help & Support',
      hindiLabel: 'à¤¸à¤¹à¤¾à¤¯à¤¤à¤¾',
      icon: 'â“',
      path: '/help'
    }
  ];

  const stats = [
    { 
      label: 'Total Complaints', 
      hindiLabel: 'à¤•à¥à¤² à¤¶à¤¿à¤•à¤¾à¤¯à¤¤à¥‡à¤‚', 
      value: userStats.totalComplaints.toString(), 
      color: 'blue',
      trend: '+12%'
    },
    { 
      label: 'Pending', 
      hindiLabel: 'à¤²à¤‚à¤¬à¤¿à¤¤', 
      value: userStats.pending.toString(), 
      color: 'amber',
      trend: '-5%'
    },
    { 
      label: 'Resolved', 
      hindiLabel: 'à¤¹à¤²', 
      value: userStats.resolved.toString(), 
      color: 'green',
      trend: '+25%'
    },
    { 
      label: 'In Progress', 
      hindiLabel: 'à¤ªà¥à¤°à¤—à¤¤à¤¿ à¤®à¥‡à¤‚', 
      value: userStats.inProgress.toString(), 
      color: 'purple',
      trend: '+8%'
    }
  ];

  const handleMenuClick = (item) => {
    setActiveSection(item.id);
    if (item.path && item.path !== '/dashboard' && item.id !== 'dashboard') {
      navigate(item.path);
    }
    setSidebarOpen(false);
  };

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'resolved':
        return 'bg-green-100 text-green-700';
      case 'in progress':
        return 'bg-purple-100 text-purple-700';
      case 'pending':
        return 'bg-amber-100 text-amber-700';
      default:
        return 'bg-gray-100 text-gray-700';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getProfileImage = () => {
    if (user?.photo) {
      if (user.photo.startsWith('http')) {
        return user.photo;
      }
      return `http://localhost:5000/${user.photo}`;
    }
    return null;
  };

  const getInitials = () => {
    if (!user?.name) return 'U';
    return user.name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  };

  const getBadgeColor = (color) => {
    const colors = {
      gold: 'bg-gradient-to-r from-yellow-400 to-yellow-600',
      blue: 'bg-gradient-to-r from-blue-400 to-blue-600',
      purple: 'bg-gradient-to-r from-purple-400 to-purple-600',
      green: 'bg-gradient-to-r from-green-400 to-green-600'
    };
    return colors[color] || 'bg-gradient-to-r from-gray-400 to-gray-600';
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-50 to-blue-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600 font-medium">Loading your dashboard...</p>
          <p className="text-sm text-gray-500">à¤†à¤ªà¤•à¤¾ à¤¡à¥ˆà¤¶à¤¬à¥‹à¤°à¥à¤¡ à¤²à¥‹à¤¡ à¤¹à¥‹ à¤°à¤¹à¤¾ à¤¹à¥ˆ...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-50 to-blue-100">
        <div className="text-center bg-white rounded-3xl p-8 shadow-xl">
          <div className="text-6xl mb-4">âš ï¸</div>
          <h2 className="text-2xl font-bold text-red-600 mb-4">Session Expired</h2>
          <p className="text-gray-600 mb-6">Please login again to continue</p>
          <button
            onClick={() => navigate('/login')}
            className="bg-blue-600 text-white px-6 py-3 rounded-xl hover:bg-blue-700 transition-colors"
          >
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        ></div>
      )}

      {/* Sidebar */}
      <div className={`fixed left-0 top-0 h-full w-80 bg-white/95 backdrop-blur-sm shadow-2xl transform transition-transform duration-300 z-50 ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0 lg:relative lg:shadow-none border-r border-white/20`}>
        
        {/* Sidebar Header */}
        <div className="p-6 border-b border-gray-200/50 bg-gradient-to-r from-blue-600 to-indigo-600 text-white">
          <div className="flex items-center space-x-4">
            <div className="w-16 h-16 bg-white/20 rounded-full flex items-center justify-center text-2xl font-bold backdrop-blur-sm border border-white/30 overflow-hidden">
              {getProfileImage() ? (
                <img 
                  src={getProfileImage()} 
                  alt="Profile" 
                  className="w-full h-full rounded-full object-cover"
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
              ) : null}
              <div className={`w-full h-full rounded-full flex items-center justify-center ${getProfileImage() ? 'hidden' : 'flex'}`}>
                {getInitials()}
              </div>
            </div>
            <div className="flex-1 min-w-0">
              <h3 className="font-bold text-lg truncate">à¤¨à¤®à¤¸à¥à¤¤à¥‡, {user.name}!</h3>
              <p className="text-blue-100 text-sm truncate">ID: {user.userId}</p>
            </div>
          </div>
        </div>

        {/* Navigation Menu */}
        <nav className="p-4 space-y-2">
          {menuItems.map((item) => (
            <button
              key={item.id}
              onClick={() => handleMenuClick(item)}
              className={`w-full flex items-center space-x-4 px-4 py-3 rounded-xl text-left transition-all duration-200 ${
                activeSection === item.id
                  ? 'bg-gradient-to-r from-blue-500 to-indigo-500 text-white shadow-lg scale-105'
                  : 'text-gray-700 hover:bg-gradient-to-r hover:from-gray-100 hover:to-blue-50 hover:text-blue-700'
              }`}
            >
              <span className="text-2xl">{item.icon}</span>
              <div className="flex-1">
                <div className="font-medium">{item.label}</div>
                <div className="text-xs opacity-75">{item.hindiLabel}</div>
              </div>
            </button>
          ))}
        </nav>

        {/* Logout Button */}
        <div className="absolute bottom-6 left-4 right-4">
          <button
            onClick={handleLogout}
            className="w-full flex items-center justify-center space-x-2 px-4 py-3 bg-gradient-to-r from-red-500 to-red-600 text-white rounded-xl hover:from-red-600 hover:to-red-700 transition-all duration-200 shadow-lg hover:shadow-xl"
          >
            <span>ðŸšª</span>
            <span className="font-medium">Logout / à¤²à¥‰à¤—à¤†à¤‰à¤Ÿ</span>
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="lg:ml-80 min-h-screen">
        {/* Top Bar */}
        <header className="bg-white/80 backdrop-blur-sm shadow-lg border-b border-white/20 sticky top-0 z-30">
          <div className="flex items-center justify-between px-6 py-4">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => setSidebarOpen(true)}
                className="lg:hidden p-2 rounded-lg bg-gray-100 hover:bg-gray-200 transition-colors"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
              <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
                à¤¶à¤¿à¤•à¤¾à¤¯à¤¤ à¤ªà¥‹à¤°à¥à¤Ÿà¤² à¤¡à¥ˆà¤¶à¤¬à¥‹à¤°à¥à¤¡
              </h1>
            </div>
            
            {/* User Info in Top Bar */}
            <div className="flex items-center space-x-4">
              <div className="hidden md:block text-right">
                <div className="font-medium text-gray-900">{user.name}</div>
                <div className="text-sm text-gray-600">{user.email}</div>
              </div>
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-full flex items-center justify-center text-white font-bold shadow-lg overflow-hidden">
                {getProfileImage() ? (
                  <img 
                    src={getProfileImage()} 
                    alt="Profile" 
                    className="w-full h-full rounded-full object-cover"
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                ) : null}
                <div className={`w-full h-full rounded-full flex items-center justify-center ${getProfileImage() ? 'hidden' : 'flex'}`}>
                  {getInitials()}
                </div>
              </div>
            </div>
          </div>
        </header>

        {/* Dashboard Content */}
        <main className="p-6">
          {/* Welcome Section */}
          <div className="bg-gradient-to-r from-blue-600 to-indigo-600 rounded-3xl p-8 text-white mb-8 shadow-2xl">
            <div className="flex flex-col md:flex-row items-start md:items-center justify-between">
              <div>
                <h2 className="text-3xl font-bold mb-2">Welcome back, {user.name}!</h2>
                <p className="text-blue-100 text-lg">à¤†à¤ªà¤•à¤¾ à¤¸à¥à¤µà¤¾à¤—à¤¤ à¤¹à¥ˆ! à¤†à¤œ à¤†à¤ª à¤•à¥à¤¯à¤¾ à¤•à¤°à¤¨à¤¾ à¤šà¤¾à¤¹à¤¤à¥‡ à¤¹à¥ˆà¤‚?</p>
              </div>
              <div className="mt-4 md:mt-0">
                <div className="bg-white/20 backdrop-blur-sm rounded-2xl p-4 border border-white/30">
                  <div className="text-sm text-blue-100">Today's Date</div>
                  <div className="text-xl font-bold">{new Date().toLocaleDateString('hi-IN')}</div>
                </div>
              </div>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-xl">
              <div className="flex items-center space-x-2 text-red-600">
                <span>âš ï¸</span>
                <span className="font-medium">{error}</span>
              </div>
            </div>
          )}

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            {stats.map((stat, index) => (
              <div key={index} className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-lg border border-white/20 hover:shadow-xl transition-all duration-300 hover:scale-105">
                <div className="flex items-center justify-between mb-4">
                  <div className={`w-12 h-12 bg-gradient-to-br from-${stat.color}-500 to-${stat.color}-600 rounded-xl flex items-center justify-center text-white text-xl font-bold shadow-lg`}>
                    {stat.value}
                  </div>
                  <div className={`text-sm font-medium ${stat.trend.startsWith('+') ? 'text-green-600' : 'text-red-600'}`}>
                    {stat.trend}
                  </div>
                </div>
                <h3 className="font-bold text-gray-900 text-lg">{stat.label}</h3>
                <p className="text-gray-600 text-sm">{stat.hindiLabel}</p>
              </div>
            ))}
          </div>

          {/* Points and Badges Section */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
            <div className="bg-white/80 backdrop-blur-sm rounded-3xl p-8 shadow-lg border border-white/20">
              <h3 className="text-2xl font-bold text-gray-900 mb-6">Your Points / à¤†à¤ªà¤•à¥‡ à¤…à¤‚à¤•</h3>
              <div className="text-center">
                <div className="w-32 h-32 mx-auto bg-gradient-to-br from-yellow-400 to-yellow-600 rounded-full flex items-center justify-center mb-4 shadow-2xl">
                  <div className="text-white">
                    <div className="text-3xl font-bold">{userPoints}</div>
                    <div className="text-sm">Points</div>
                  </div>
                </div>
                <p className="text-gray-600">Keep filing and resolving complaints to earn more points!</p>
                <p className="text-sm text-gray-500">à¤…à¤§à¤¿à¤• à¤…à¤‚à¤• à¤…à¤°à¥à¤œà¤¿à¤¤ à¤•à¤°à¤¨à¥‡ à¤•à¥‡ à¤²à¤¿à¤ à¤¶à¤¿à¤•à¤¾à¤¯à¤¤à¥‡à¤‚ à¤¦à¤°à¥à¤œ à¤•à¤°à¤¤à¥‡ à¤°à¤¹à¥‡à¤‚!</p>
              </div>
            </div>

            <div className="bg-white/80 backdrop-blur-sm rounded-3xl p-8 shadow-lg border border-white/20">
              <h3 className="text-2xl font-bold text-gray-900 mb-6">Your Badges / à¤†à¤ªà¤•à¥‡ à¤¬à¥ˆà¤œ</h3>
              <div className="grid grid-cols-2 gap-4">
                {userBadges.length > 0 ? (
                  userBadges.map((badge, index) => (
                    <div key={index} className={`${getBadgeColor(badge.color)} p-4 rounded-2xl text-white text-center shadow-lg`}>
                      <div className="text-3xl mb-2">{badge.icon}</div>
                      <div className="font-bold text-sm">{badge.name}</div>
                    </div>
                  ))
                ) : (
                  <div className="col-span-2 text-center py-8">
                    <div className="text-4xl mb-4">ðŸ†</div>
                    <p className="text-gray-600">Complete more activities to earn badges!</p>
                    <p className="text-sm text-gray-500">à¤¬à¥ˆà¤œ à¤…à¤°à¥à¤œà¤¿à¤¤ à¤•à¤°à¤¨à¥‡ à¤•à¥‡ à¤²à¤¿à¤ à¤…à¤§à¤¿à¤• à¤—à¤¤à¤¿à¤µà¤¿à¤§à¤¿à¤¯à¤¾à¤‚ à¤ªà¥‚à¤°à¥€ à¤•à¤°à¥‡à¤‚!</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Analytics Charts */}
