import React, { useState } from "react";
import { Search, CheckCircle, Clock, AlertCircle, MapPin, Building, FileText, User, ArrowRight, Shield, Calendar, Phone } from "lucide-react";

export default function TrackStatus() {
  const [complaintId, setComplaintId] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleTrack = async () => {
    if (!complaintId.trim()) return;
    
    setIsLoading(true);
    try {
      // Replace axios with fetch for demo purposes
      const res = await fetch(`http://localhost:5000/api/complaint/${complaintId.trim()}`);
      const data = await res.json();

      if (data.success) {
        setResult(data);
        setError("");
      } else {
        setResult(null);
        setError("शिकायत नहीं मिली। कृपया सही Complaint ID दर्ज करें।");
      }
    } catch (err) {
      setResult(null);
      setError("सर्वर से कनेक्ट नहीं हो सका या शिकायत नहीं मिली।");
    } finally {
      setIsLoading(false);
    }
  };

  const getStatusStep = (status) => {
    const statusLower = status?.toLowerCase() || '';
    if (statusLower.includes('पूर्ण') || statusLower.includes('complete') || statusLower.includes('resolved')) {
      return 3;
    } else if (statusLower.includes('प्रगति') || statusLower.includes('progress') || statusLower.includes('pending')) {
      return 2;
    }
    return 1;
  };

  const steps = [
    { 
      id: 1, 
      title: "शिकायत दर्ज", 
      desc: "आपकी शिकायत प्राप्त हुई",
      icon: Shield,
      color: "bg-blue-500"
    },
    { 
      id: 2, 
      title: "समीक्षाधीन", 
      desc: "विभाग द्वारा जांच की जा रही है",
      icon: Clock,
      color: "bg-yellow-500"
    },
    { 
      id: 3, 
      title: "पूर्ण", 
      desc: "शिकायत का समाधान हो गया",
      icon: CheckCircle,
      color: "bg-green-500"
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navigation */}
      <nav className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center gap-3">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-600 to-purple-600 rounded-lg flex items-center justify-center">
                <Shield className="w-5 h-5 text-white" />
              </div>
              <span className="text-xl font-bold text-gray-900">शिकायत पोर्टल</span>
            </div>
            <div className="text-sm text-gray-600">
              <Calendar className="inline w-4 h-4 mr-1" />
              {new Date().toLocaleDateString('hi-IN')}
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid lg:grid-cols-3 gap-8">
          
          {/* Left Sidebar - Search */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-2xl shadow-lg p-6 sticky top-6">
              <div className="text-center mb-6">
                <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-purple-600 rounded-2xl mx-auto mb-4 flex items-center justify-center">
                  <Search className="w-8 h-8 text-white" />
                </div>
                <h2 className="text-xl font-bold text-gray-900 mb-2">शिकायत ट्रैकर</h2>
                <p className="text-gray-600 text-sm">अपनी शिकायत की स्थिति देखें</p>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Complaint ID
                  </label>
                  <input
                    type="text"
                    placeholder="CMP123456"
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-center font-mono text-lg tracking-wider"
                    value={complaintId}
                    onChange={(e) => setComplaintId(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleTrack()}
                  />
                </div>

                <button
                  onClick={handleTrack}
                  disabled={isLoading || !complaintId.trim()}
                  className="w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 disabled:from-gray-400 disabled:to-gray-500 text-white py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:scale-[1.02] transition-all duration-200 disabled:transform-none flex items-center justify-center gap-2"
                >
                  {isLoading ? (
                    <>
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                      ट्रैक कर रहे हैं...
                    </>
                  ) : (
                    <>
                      <Search className="w-5 h-5" />
                      ट्रैक करें
                    </>
                  )}
                </button>

                {error && (
                  <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-xl">
                    <div className="flex items-center gap-3">
                      <AlertCircle className="w-5 h-5 text-red-500 flex-shrink-0" />
                      <p className="text-red-700 text-sm">{error}</p>
                    </div>
                  </div>
                )}

                {/* Quick Info */}
                <div className="mt-6 pt-6 border-t border-gray-200">
                  <h3 className="font-semibold text-gray-900 mb-3">सहायता</h3>
                  <div className="space-y-2 text-sm text-gray-600">
                    <div className="flex items-center gap-2">
                      <Phone className="w-4 h-4" />
                      हेल्पलाइन: 1800-123-456
                    </div>
                    <div className="flex items-center gap-2">
                      <Clock className="w-4 h-4" />
                      सेवा समय: 9 AM - 6 PM
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Right Content - Results */}
          <div className="lg:col-span-2">
            {!result && !error && (
              <div className="bg-white rounded-2xl shadow-lg p-12 text-center">
                <div className="w-20 h-20 bg-gray-100 rounded-2xl mx-auto mb-6 flex items-center justify-center">
                  <Search className="w-10 h-10 text-gray-400" />
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                  अपनी शिकायत खोजें
                </h3>
                <p className="text-gray-600">
                  कृपया बाईं ओर अपनी Complaint ID दर्ज करें और ट्रैक करें बटन दबाएं
                </p>
              </div>
            )}

            {result && (
              <div className="space-y-6 animate-fadeInUp">
                {/* Status Timeline */}
                <div className="bg-white rounded-2xl shadow-lg p-8">
                  <h3 className="text-2xl font-bold text-gray-900 mb-8">शिकायत की स्थिति</h3>
                  
                  <div className="flex items-center justify-between mb-8">
                    {steps.map((step, index) => {
                      const isActive = getStatusStep(result.status) >= step.id;
                      const isCurrent = getStatusStep(result.status) === step.id;
                      
                      return (
                        <div key={step.id} className="flex items-center">
                          <div className="flex flex-col items-center">
                            <div className={`w-12 h-12 rounded-full flex items-center justify-center transition-all duration-500 ${
                              isActive ? `${step.color} text-white shadow-lg` : 'bg-gray-200 text-gray-400'
                            } ${isCurrent ? 'ring-4 ring-blue-100 scale-110' : ''}`}>
                              <step.icon className="w-6 h-6" />
                            </div>
                            <div className="text-center mt-3 max-w-[100px]">
                              <p className={`font-semibold text-sm ${isActive ? 'text-gray-900' : 'text-gray-500'}`}>
                                {step.title}
                              </p>
                              <p className="text-xs text-gray-600 mt-1">{step.desc}</p>
                            </div>
                          </div>
                          
                          {index < steps.length - 1 && (
                            <div className={`flex-1 h-1 mx-4 rounded-full transition-all duration-500 ${
                              getStatusStep(result.status) > step.id ? 'bg-green-400' : 'bg-gray-200'
                            }`}></div>
                          )}
                        </div>
                      );
                    })}
                  </div>

                  <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <h4 className="text-lg font-semibold text-gray-900">वर्तमान स्थिति</h4>
                        <p className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                          {result.status}
                        </p>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-gray-600">Complaint ID</p>
                        <p className="font-mono font-bold text-gray-900">{complaintId}</p>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Details Cards */}
                <div className="grid md:grid-cols-2 gap-6">
                  <div className="bg-white rounded-2xl shadow-lg p-6 border-l-4 border-blue-500">
                    <div className="flex items-center gap-4 mb-4">
                      <div className="p-3 bg-blue-100 rounded-xl">
                        <User className="w-6 h-6 text-blue-600" />
                      </div>
                      <div>
                        <h4 className="text-lg font-semibold text-gray-900">शिकायतकर्ता की जानकारी</h4>
                      </div>
                    </div>
                    <p className="text-2xl font-bold text-gray-900 mb-2">{result.name}</p>
                    <p className="text-gray-600">नाम</p>
                  </div>

                  <div className="bg-white rounded-2xl shadow-lg p-6 border-l-4 border-green-500">
                    <div className="flex items-center gap-4 mb-4">
                      <div className="p-3 bg-green-100 rounded-xl">
                        <MapPin className="w-6 h-6 text-green-600" />
                      </div>
                      <div>
                        <h4 className="text-lg font-semibold text-gray-900">स्थान की जानकारी</h4>
                      </div>
                    </div>
                    <p className="text-2xl font-bold text-gray-900 mb-2">{result.location}</p>
                    <p className="text-gray-600">पता</p>
                  </div>

                  <div className="bg-white rounded-2xl shadow-lg p-6 border-l-4 border-purple-500">
                    <div className="flex items-center gap-4 mb-4">
                      <div className="p-3 bg-purple-100 rounded-xl">
                        <Building className="w-6 h-6 text-purple-600" />
                      </div>
                      <div>
                        <h4 className="text-lg font-semibold text-gray-900">संबंधित विभाग</h4>
                      </div>
                    </div>
                    <p className="text-2xl font-bold text-gray-900 mb-2">{result.department}</p>
                    <p className="text-gray-600">जिम्मेदार विभाग</p>
                  </div>

                  <div className="bg-white rounded-2xl shadow-lg p-6 border-l-4 border-orange-500">
                    <div className="flex items-center gap-4 mb-4">
                      <div className="p-3 bg-orange-100 rounded-xl">
                        <Calendar className="w-6 h-6 text-orange-600" />
                      </div>
                      <div>
                        <h4 className="text-lg font-semibold text-gray-900">दिनांक</h4>
                      </div>
                    </div>
                    <p className="text-2xl font-bold text-gray-900 mb-2">
                      {new Date().toLocaleDateString('hi-IN')}
                    </p>
                    <p className="text-gray-600">शिकायत दर्ज की तारीख</p>
                  </div>
                </div>

                {/* Description */}
                <div className="bg-white rounded-2xl shadow-lg p-8">
                  <div className="flex items-center gap-4 mb-6">
                    <div className="p-3 bg-indigo-100 rounded-xl">
                      <FileText className="w-6 h-6 text-indigo-600" />
                    </div>
                    <h4 className="text-xl font-semibold text-gray-900">शिकायत का विस्तृत विवरण</h4>
                  </div>
                  <div className="bg-gray-50 rounded-xl p-6">
                    <p className="text-gray-800 leading-relaxed text-lg">{result.description}</p>
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="bg-white rounded-2xl shadow-lg p-6">
                  <div className="flex flex-wrap gap-4">
                    <button className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-xl font-semibold transition-colors flex items-center gap-2">
                      <ArrowRight className="w-5 h-5" />
                      अपडेट के लिए सूचना पाएं
                    </button>
                    <button className="px-6 py-3 bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-xl font-semibold transition-colors">
                      शिकायत प्रिंट करें
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .animate-fadeInUp {
          animation: fadeInUp 0.6s ease-out;
        }
      `}</style>
    </div>
  );
}
